mapper.select:
select
  id, name, introduction, scope, status, address, create_date, representative, fund, category, publish
from company


        and id = #{id}


        and name = #{name}


        and introduction = #{introduction}


        and scope = #{scope}


        and status = #{status}


        and address = #{address}


        and create_date = #{createDate}


        and representative = #{representative}


        and fund = #{fund}


        and category = #{category}


        and publish = #{publish}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from company


        and id = #{id}


        and name = #{name}


        and introduction = #{introduction}


        and scope = #{scope}


        and status = #{status}


        and address = #{address}


        and create_date = #{createDate}


        and representative = #{representative}


        and fund = #{fund}


        and category = #{category}


        and publish = #{publish}
